        <input
          type="date"
          value={theDate}
          onChange={onDateChangeHandler}
          required
        />
        <label>Title</label>
        <input
          type="text"
          value={myTask}
          onChange={onTitleChangeHandler}
          required
        />
        <label>Description</label>
        <input
          type="text"
          value={myDescription}
          onChange={onDescriptionChangeHandler}
          required
        />
